/* eslint-disable react/no-unescaped-entities */
import React, {useState} from 'react';
import styled from 'styled-components';

import Navbar from '../../components/shared/Navbar';
import DragAndDropZone from './components/DragAndDropZone';
import KeywordSearch from './components/KeywordSearch';
import Header from './components/Header';
import SearchResults from './components/SearchResults';

const SplitScreenLayout = styled.div`
  display: flex;
  & > * {
    width: 50%;
  }
`;

const BodyContainer = styled.div`
  padding: 0 9rem;
`;

const Home = () => {
  const [uploadedFiles, setuploadedFiles] = useState([]);

  const onDrop = event => {
    event.preventDefault();
    setuploadedFiles(Array.from(event.dataTransfer.files));
  };

  const onFileRemove = deleteKey => {
    // remove file from state.
    setuploadedFiles(
      uploadedFiles.filter((file, index) => index !== deleteKey),
    );
  };

  const onSubmit = data => {
    console.log(data);
    // uploadedFiles to formdata
    const formData = new FormData();
    const studentsList =
      streamState.streamLive.selectedStudents.length === 0
        ? students.map(stud => stud.id)
        : streamState?.streamLive.selectedStudents;

    formData.append('post_content', data.post_content);
    formData.append('unit_id', data.unit);
    studentsList.forEach(stud => formData.append('students[]', stud));
    selectedFiles.forEach(file => formData.append('files[]', file, file.name));
    setSelectedFiles([]);
    dispatch(createPost(formData));
  };

  <>
    <Navbar />
    <BodyContainer>
      <Header />
      <SplitScreenLayout>
        <DragAndDropZone
          uploadedFiles={uploadedFiles}
          onDrop={onDrop}
          onFileRemove={onFileRemove}
        />
        <KeywordSearch onSubmit={onSubmit} />
      </SplitScreenLayout>
      <SearchResults />
    </BodyContainer>
  </>;
};

export default Home;
