/* eslint-disable react/no-unescaped-entities */
import React, {useState} from 'react';
import styled from 'styled-components';

import Navbar from '../../components/shared/Navbar';
import DragAndDropZone from './components/DragAndDropZone';
import KeywordSearch from './components/KeywordSearch';
import Header from './components/Header';
import SearchResults from './components/SearchResults';

const SplitScreenLayout = styled.div`
  display: flex;
  & > * {
    width: 50%;
  }
`;

const BodyContainer = styled.div`
  padding: 0 9rem;
`;

const Home = () => {
  const [uploadedFiles, setuploadedFiles] = useState([]);

  const onDrop = event => {
    event.preventDefault();
    setuploadedFiles(Array.from(event.dataTransfer.files));
  };

  const onFileRemove = deleteKey => {
    // remove file from state.
    setuploadedFiles(
      uploadedFiles.filter((file, index) => index !== deleteKey),
    );
  };

  <>
    <Navbar />
    <BodyContainer>
      <Header />
      <SplitScreenLayout>
        <DragAndDropZone onDrop={onDrop} onFileRemove={onFileRemove} />
        <KeywordSearch />
      </SplitScreenLayout>
      <SearchResults />
    </BodyContainer>
  </>;
};

export default Home;
