import styled from 'styled-components';

import FileUploadIcon from '../../../assets/icons/file_upload.svg';

const StyledDropZone = styled.div`
  display: flex;
  justify-content: center;
  height: 100%;
`;

const StyledDropZoneContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 50%;
  box-shadow: 1px 1px 5px 0px rgba(209, 186, 209, 1);
  justify-content: center;
  background-color: #fef5fd;
  img {
    width: 100px;
  }
`;

const DropZone = () => (
  <StyledDropZone>
    <h3>C.V keyword lookup</h3>
    <ol>
      <li>Upload multiple C.V's.</li>
      <li>Add terms to search for.</li>
      <li>Get C.V's that match the terms.</li>
    </ol>
    <StyledDropZoneContainer>
      <img src={FileUploadIcon} alt="file upload icon" />
      <h3> Upload your files </h3>
      <p> Drop your files here or browse. </p>
    </StyledDropZoneContainer>
  </StyledDropZone>
);

export default DropZone;
