/* eslint-disable no-unused-vars */
/* eslint-disable react/no-unescaped-entities */
import React, {useState} from 'react';
import styled from 'styled-components';

import FileUploadIcon from '../../../assets/icons/file_upload.svg';

const StyledDropZone = styled.div`
  display: flex;
  justify-content: center;
  height: 100%;
`;

const StyledDropZoneContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 50%;
  box-shadow: 1px 1px 5px 0px rgba(209, 186, 209, 1);
  justify-content: center;
  background-color: #fef5fd;
  img {
    width: 100px;
  }
  h3 {
    margin-bottom: 0.5rem;
  }
`;

const DropZone = () => {
  const [uploadedFiles, setuploadedFiles] = useState([]);

  const onDragEnter = event => {
    event.preventDefault();
    console.log('dragEnter!');
  };

  const onDragLeave = event => {
    event.preventDefault();
    console.log('dragLeave!');
  };

  const onDragOver = event => {
    event.preventDefault();
    console.log('dragover');
  };

  const onDrop = event => {
    event.preventDefault();
    console.log(event);
    setuploadedFiles(event.dataTransfer.files);
    console.log('dropped!!!');
  };

  return (
    <StyledDropZone
      onDragEnter={onDragEnter}
      onDragLeave={onDragLeave}
      onDragOver={onDragOver}
      onDrop={onDrop}
    >
      <StyledDropZoneContainer>
        <img src={FileUploadIcon} alt="file upload icon" />
        <h3> Upload your files </h3>
        <p>
          Drop your files here or <b> browse .</b>
        </p>
      </StyledDropZoneContainer>
    </StyledDropZone>
  );
};

export default DropZone;
